INSERT INTO `text` (`id`, `lang`, `text`) VALUES
('guide-dashboard-user-profile', 'fr', 'Si vous voulez présenter un projet ou participer comme cofinanceur, il faut faire partie de la communauté de Goteo. Nous vous recommandons donc de porter attention à votre texte de présentation, de donner les liens pertinents sur vos activités et d''ajouter une photo permettant de vous identifier.'),
('guide-dashboard-user-profile', 'it', 'Whether you want to create a project or co-finance someone else''s, in order to join the Goteo community, we recommend that you carefully edit your presentation text, and include relevant links about what you do as well as a profile image with which you identify'),
('guide-dashboard-user-profile', 'pl', 'Whether you want to create a project or co-finance someone else''s, in order to join the Goteo community, we recommend that you carefully edit your presentation text, and include relevant links about what you do as well as a profile image with which you identify'),
