INSERT INTO `template` VALUES(65, 'Agradecimiento recarga monedero', 'invest', 'Mensaje al usuario al recargar su monedero', 'Muchas gracias por recargar tu monedero!', 'Hola %USERNAME%! <p>Tu monedero acaba de ser recargado con %AMOUNT_RECHARGUED%.</p><p>En este momento dispones de un total de %AMOUNT_RECHARGUED% en <a href="%WALLET_URL%">tu monedero</a> para invertir en proyectos de Goteo.</p><p>Te recordamos que la recarga de crédito otorga la posibilidad de <a href="%CERTIFICATE_URL%">descargar un certificado de desgravación fiscal</a></p>');
INSERT INTO `template` VALUES(66, 'Aviso proyecto favorito', 'invest', 'Mensaje al usuario recordando proyecto favorito', 'Te recordamos que la campaña %PROJECTNAME% esta a punto de terminar', 'Hola %USERNAME%! <p>Te recordamos que a la campaña <a href="%PROJECTURL%">%PROJECTNAME%</a> le quedan %DAYS_LEFT% días y %MONEY_LEFT% para alcanzar su presupuesto mínimo y necesita tu ayuda para conseguirlo.</p><p>Gota a gota… ¡el río suena! :)</p><p>El equipo de Goteo</p>');
